function getCookie(b){var c=document.cookie;var d=c.indexOf(" "+b+"=");if(d==-1){d=c.indexOf(b+"=")}if(d==-1){c=null}else{d=c.indexOf("=",d)+1;var a=c.indexOf(";",d);if(a==-1){a=c.length}c=unescape(c.substring(d,a))}return c}function checkCookie(){var a=getCookie("username");if(a!=null&&a!=""){alert("Welcome again "+a)}else{a=prompt("Please enter your name:","");if(a!=null&&a!=""){setCookie("username",a,365)}}}function setCookie(a,d,b){var e=new Date();e.setDate(e.getDate()+b);var c=escape(d)+((b==null)?"":"; expires="+e.toUTCString());document.cookie=a+"="+c};